<script>
  import { bus } from "../../../app";

  export default {
    props:['url','name'],
    data () {
      return {
        profile_tab:'1',
        user:[],
        errors:[],
        success:null,
      }
    },

    methods:{
      getData()
      {
        axios.get('/admin/parent/editlist/'+this.name).then(response => {
          this.user = response.data;
          //console.log(this.user);     
        });
      },
    },
    
    created()
    {      
      this.getData(); 

      bus.$on("dataProfileTab", data => {
        if(data!='')
        {
          this.profile_tab=data;                   
        }
      });
     
    }
  }
</script>